import React from 'react';
import { useState } from 'react';
import { Container, Form } from "react-bootstrap";
import SpotifyWebApi from 'spotify-web-api-node';
import useAuth from '../../useAuth';
import { useEffect } from 'react';


const spotifyApi = new SpotifyWebApi({
    clientId: "6bc52007f6114854ad75a84a1d119b23"
   })
   


function Album({accessToken, trackUri}){    


    const [search, setSearch] = useState("")
    const [searchResults, setSearchResults] = useState([])
    const [image, setImage] = useState("https://i.scdn.co/image/ab67616d0000b273df55e326ed144ab4f5cecf95")

     
    useEffect(() =>{
        if(!accessToken) return
        spotifyApi.setAccessToken(accessToken)
    }, [accessToken])



    useEffect(() => {
        if(!search) return setSearchResults([])
        if(!accessToken) return ("Pas réussi a choper le token")   
        let cancel = false
        

        spotifyApi.searchTracks(search).then(res =>{
            if(cancel) return

        res.body.tracks.items.map(track => {
            const smallestAlbumImage = track.album.images.reduce(
              (smallest, image) => {
                if (image.height < smallest.height) return image
                return smallest
              },
              track.album.images[0]
            )
  
            return {
              artist: track.artists[0].name,
              title: track.name,
              uri: track.uri,
              albumUrl: smallestAlbumImage.url,
            }
          })
        })
    return () => cancel = true
    }, [search, accessToken])

  
   

return (
        <div>       
        <Form.Control type="search"
         placeholder="Search Songs/Artists"
         value={search}
         onChange = {e => setSearch(e.target.value)}/> 
            <div>
                <img 
                src={image}
                alt="Song"
                />
            </div>          
        </div>
    )
}

export default Album;
