import React from 'react';
import { useState } from 'react';
import SpotifyWebApi from 'spotify-web-api-node';
import { useEffect } from 'react';
import "./album.css"
import Player from '../Player';


const spotifyApi = new SpotifyWebApi({
    clientId: "6bc52007f6114854ad75a84a1d119b23"    
   })
   


function Album({accessToken, search, setSearch}) {    


    const [searchResults, setSearchResults] = useState([])
    const [image, setImage] = useState("https://i.scdn.co/image/ab67616d0000b273df55e326ed144ab4f5cecf95")
    const [titre, setTitre] = useState([])
    const [artist, setArtist] = useState([]) 
    const [playingTrack, setPlayingTrack] = useState()

   // const [search, setSearch] = useState("")

    
     
    
    useEffect(() =>{
        if(!accessToken) return
        spotifyApi.setAccessToken(accessToken)
    }, [accessToken])



    useEffect(() => {
        if(!search) return setSearchResults([])
        if(!accessToken) return ("Pas réussi a choper le token")   
        let cancel = false
        


        spotifyApi.searchTracks(search).then(res =>{
            if(cancel) return

        const searchInApi = res.body.tracks.items[0]
        
        console.log("data:", res)
        //console.log("la voila:", res.body.tracks.items[0].album.images[0].url)
        setImage(searchInApi.album.images[0].url)
        setArtist(searchInApi.artists[0].name)
        setTitre(searchInApi.name)
        setPlayingTrack(searchInApi.uri)
        
        })
        

    return () => cancel = true    
    }, [search, accessToken])

    

  
   

return (
        <div> 
                <div>
                    POCHETTE DE L'ALBUM : 
                <img 
                width="300" 
                height="300"
                src={image}
                alt="Song" 
                className="center"                            
                />                
            </div> 
        <div>Titre : {titre}</div>
        <div>Artiste : {artist} </div> 
               <div><Player accessToken = {accessToken} playingTrack={playingTrack?.uri}/></div>   
               <form id="searchbox" method="get" action="/search" autocomplete="off">
    <input name="q" type="text" size="20" placeholder="Search Songs/Artists"
         value={search}
         onChange = {e => setSearch(e.target.value)} />
        </form>                   
        </div>
    )


}

export default Album;
