import React, { Component } from 'react';
import Home from '../page/home/Home';
import './affichage.css';
import Barre_nav from "./barre_nav/Barre_nav";
import Album from './Widgets/Album';





function Affichage({accessToken, search}) {

const setSearch = useState([])
 console.log("recherche :", search)
  return (
    <div>
       <form id="searchbox" method="get" action="/search" autocomplete="off">
    <input name="q" type="text" size="15" placeholder="Search Songs/Artists"
         value={search}
         onChange = {e => setSearch(e.target.value)} />
        </form>   
      <div className="container">
        <Barre_nav/>
        <div className="main">
            <Home accessToken={accessToken} />
        </div>
      </div>
      <footer className="footer">Copyright Lucas VAGNER & Thibault TREBUCHON</footer>
    </div>
  );
}

export default Affichage;
